import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ListTotal = () => {
  const [lists, setLists] = useState([]);
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then((res) => res.json())
      .then((res) => setLists(res));
  }, []);
  return (
    <div>
      <Title>게시물 목록</Title>
      <Ul>
        {lists.map(({ id, title, body }) => {
          <Li>
            <span className='idName'>아이디</span>
            <span className='listBody'>{title}</span>
          </Li>;
        })}
      </Ul>
    </div>
  );
};

export default ListTotal;

const Title = styled.h1`
  font-size: 1.5rem;
  color: #b66666;
  text-align: center;
  margin-top: 2rem;
`;

const Ul = styled.ul`
  font-size: 0.875rem;
  margin: 3rem auto;
  width: 1200px;
`;

const Li = styled.li`
  border-bottom: 1px solid #ccc;
  padding: 0.5rem 1rem;
  display: flex;
  & > .idName {
    width: 3rem;
    color: #333;
  }
  & > .listBody {
    width: 50rem;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
    color: #666;
    text-align: left;
  }
  & > .listBody:hover {
    color: #000;
  }
  &:hover {
    background: rgba(0, 0, 0, 0.1);
    cursor: pointer;
  }
`;
