import React from 'react';
import TodoItem from '../TodoItem';

const TodoList = ({ todos, selectedTodoIndex, setSelectedTodoIndex }) => {
  return (
    <div>
      {todos.map((todo, index) => {
        return (
          <TodoItem
            key={index}
            todo={todo}
            onClickItem={() => {
              setSelectedTodoIndex(index);
            }}
          >
            {todo.title}
          </TodoItem>
        );
      })}
    </div>
  );
};

export default TodoList;
